// Initialize node execution records
const nodes = $input.item.json.nodes;
const execution_id = $input.item.json.execution_id;

const nodeExecutions = nodes.map(node => ({
  execution_id: execution_id,
  node_id: node.id,
  node_type: node.data.type,
  status: 'pending'
}));

return nodeExecutions.map(item => ({ json: item }));

// Execute each node sequentially
const nodes = $input.first().json.nodes;
const execution_id = $input.first().json.execution_id;
const results = [];

for (const node of nodes) {
  try {
    // Update node status to 'running'
    await $http.request({
      method: 'POST',
      url: $env.SUPABASE_URL + '/rest/v1/node_executions',
      headers: {
        'apikey': $env.SUPABASE_ANON_KEY,
        'Authorization': 'Bearer ' + $env.SUPABASE_ANON_KEY,
        'Content-Type': 'application/json',
        'Prefer': 'return=minimal'
      },
      body: {
        execution_id: execution_id,
        node_id: node.id,
        status: 'running',
        started_at: new Date().toISOString()
      }
    });

    // Simulate node execution (replace with actual node logic)
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Update node status to 'success'
    await $http.request({
      method: 'PATCH',
      url: $env.SUPABASE_URL + '/rest/v1/node_executions?execution_id=eq.' + execution_id + '&node_id=eq.' + node.id,
      headers: {
        'apikey': $env.SUPABASE_ANON_KEY,
        'Authorization': 'Bearer ' + $env.SUPABASE_ANON_KEY,
        'Content-Type': 'application/json',
        'Prefer': 'return=minimal'
      },
      body: {
        status: 'success',
        completed_at: new Date().toISOString(),
        output_data: { message: 'Node executed successfully' }
      }
    });

    results.push({ node_id: node.id, status: 'success' });
  } catch (error) {
    // Update node status to 'error'
    await $http.request({
      method: 'PATCH',
      url: $env.SUPABASE_URL + '/rest/v1/node_executions?execution_id=eq.' + execution_id + '&node_id=eq.' + node.id,
      headers: {
        'apikey': $env.SUPABASE_ANON_KEY,
        'Authorization': 'Bearer ' + $env.SUPABASE_ANON_KEY,
        'Content-Type': 'application/json',
        'Prefer': 'return=minimal'
      },
      body: {
        status: 'error',
        completed_at: new Date().toISOString(),
        error_message: error.message
      }
    });

    results.push({ node_id: node.id, status: 'error', error: error.message });
  }
}

return [{ json: { execution_id, results } }];
